mapper.select:
  	select * from
	   (select a.*,rownum row_num from
	      (select * from ZLNF_APPPIONTORDERTIME t 
	      WHERE 1=1 

	        AND t.FOODCODE=#{foodcode,jdbcType=VARCHAR}


	        AND t.STATE=#{state,jdbcType=VARCHAR}


	        AND t.COOPCODE=#{coopcode,jdbcType=VARCHAR}


	        AND t.CREATORCODE=#{creatorcode,jdbcType=VARCHAR}

	      order by t.id desc) a
	   ) b 
	where b.row_num between 1+10*(#{number}-1) and 10+10*(#{number}-1)

select * from(  select t.* ,to_char(t.APPIONTTIME,'yyyy-mm-dd') appionttimestr from ZLNF_APPPIONTORDERTIME t 
  WHERE 1=1 

    AND t.FOODCODE=#{foodcode,jdbcType=VARCHAR}


    AND t.STATE=#{state,jdbcType=VARCHAR}
  ) tt where tt.appionttimestr=#{appionttimestr}
