mapper.select:
  	select * from
	   (select a.*,rownum row_num from
	      (select * from ZLNF_APPPIONTORDER t 
	      WHERE t.ISDELETED='0' AND t.CREATORCODE=#{creatorcode,jdbcType=VARCHAR}

	        and t.STATE = #{state,jdbcType=VARCHAR}


	        and t.ORDERCODE = #{ordercode,jdbcType=VARCHAR}

	      order by t.id desc) a
	   ) b 
	where b.row_num between 1+10*(#{number}-1) and 10+10*(#{number}-1)
