mapper.select:
  	select * from
	   (select a.*,rownum row_num from
	      (select * from ZLNF_SERVICEPROGRAM t 
	      WHERE t.ISDELETED='0' AND t.MERCHANTCODE=#{merchantcode,jdbcType=VARCHAR}

	        and t.SERROTYPENCODE = #{serrotypencode,jdbcType=VARCHAR} 

	      order by t.id desc) a
	   ) b 
	where b.row_num between 1+10*(#{number}-1) and 10+10*(#{number}-1)
